package ch.bbw.zork;/** * Author:  Michael Kolling, Version: 1.1, Date: August 2000 * refactoring: Rinaldo Lanza, September 2020 */import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.stream.Collectors;public class Room {		private String description;	private HashMap<String, Room> exits;	private boolean winnRoom = false;	private ArrayList<Item> items = new ArrayList<>();	private Item itemToEnter;	private boolean isOpen;	private ConversationPice conversationPice;	public Room(String description) {		this.description = description;		this.exits = new HashMap<>();	}	public void setParameter(Room north, Room east, Room south, Room west) {		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);	}	public void setParameter(Room north, Room east, Room south, Room west, ArrayList<Item> items) {		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);		this.items.clear();		for (Item item : items){			this.items.add(item);		}	}	public void setParameter(Room north, Room east, Room south, Room west, boolean winnRoom) {		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);		this.winnRoom = winnRoom;	}	public void setParameter(Room north, Room east, Room south, Room west, Item itemToEnter) {		this.itemToEnter = itemToEnter;		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);	}	public void setParameter(Room north, Room east, Room south, Room west, ArrayList<Item> items, Item itemToEnter) {		this.itemToEnter = itemToEnter;		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);		this.items.clear();		for (Item item : items){			this.items.add(item);		}	}	public void setParameter(Room north, Room east, Room south, Room west, boolean winnRoom, Item itemToEnter) {		this.itemToEnter = itemToEnter;		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);		this.winnRoom = winnRoom;	}	public void setParameter(Room north, Room east, Room south, Room west, ConversationPice conversationPice) {		this.conversationPice = conversationPice;		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);	}	public void setParameter(Room north, Room east, Room south, Room west, ArrayList<Item> items, ConversationPice conversationPice) {		this.conversationPice = conversationPice;		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);		this.items.clear();		for (Item item : items){			this.items.add(item);		}	}	public void setParameter(Room north, Room east, Room south, Room west, boolean winnRoom, ConversationPice conversationPice) {		this.conversationPice = conversationPice;		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);		this.winnRoom = winnRoom;	}	public void setParameter(Room north, Room east, Room south, Room west, Item itemToEnter, ConversationPice conversationPice) {		this.conversationPice = conversationPice;		this.itemToEnter = itemToEnter;		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);	}	public void setParameter(Room north, Room east, Room south, Room west, ArrayList<Item> items, Item itemToEnter, ConversationPice conversationPice) {		this.conversationPice = conversationPice;		this.itemToEnter = itemToEnter;		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);		this.items.clear();		for (Item item : items){			this.items.add(item);		}	}	public void setParameter(Room north, Room east, Room south, Room west, boolean winnRoom, Item itemToEnter, ConversationPice conversationPice) {		this.conversationPice = conversationPice;		this.itemToEnter = itemToEnter;		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);		this.winnRoom = winnRoom;	}	public String shortDescription() {		return description;	}	public String longDescription() {		StringBuilder stringBuilder = new StringBuilder("You are in " + description + ".\n");		stringBuilder.append(exitString());		return stringBuilder.toString();	}	private String exitString() {		return "Exits:" + String.join(" ", exits.keySet());	}	public Room nextRoom(String direction) {		return exits.get(direction);	}	public boolean getWinnRoom(){		return winnRoom;	}	public ArrayList<Item> getItems() {		return items;	}	public void setItems(ArrayList<Item> items) {		this.items = new ArrayList<>(items);	}	public String getDescription() {		return description;	}	public void setDescription(String description) {		this.description = description;	}	public HashMap<String, Room> getExits() {		return exits;	}	public void setExits(HashMap<String, Room> exits) {		this.exits = exits;	}	public boolean isWinnRoom() {		return winnRoom;	}	public void setWinnRoom(boolean winnRoom) {		this.winnRoom = winnRoom;	}	public Item getItemToEnter() {		return itemToEnter;	}	public void setItemToEnter(Item itemToEnter) {		this.itemToEnter = itemToEnter;	}	public boolean isOpen() {		return isOpen;	}	public void setOpen(boolean open) {		isOpen = open;	}	public ConversationPice getConversationPice() {		return conversationPice;	}	public void setConversationPice(ConversationPice conversationPice) {		this.conversationPice = conversationPice;	}}